/*
@description: Returns the Location resource specified by the given reference.
*/
define fluent function getLocation(reference Reference):
  singleton from (
    [Location] L where reference.references(L)
  )

/*
@description: Converts the given FHIR [CodeableConcept](https://hl7.org/fhir/datatypes.html#CodeableConcept) value to a CQL Concept.
*/
define function ToConcept(concept FHIR.CodeableConcept):
    if concept is null then
        null
    else
        System.Concept {
            codes: concept.coding C return ToCode(C),
            display: concept.text.value
        }

/*
@description: Converts the given FHIR [Coding](https://hl7.org/fhir/datatypes.html#Coding) value to a CQL Code.
*/
define function ToCode(coding FHIR.Coding):
    if coding is null then
        null
    else
        System.Code {
          code: coding.code.value,
          system: coding.system.value,
          version: coding.version.value,
          display: coding.display.value
        }

// ----------------------------------------------------------------------
// Core encounter logic
// ----------------------------------------------------------------------

// Encounters with NHSN class codes (IMP, ACUTE, NONAC, SS)





define "Encounters with NHSN Inpatient Class":
  [Encounter] Enc
    where Enc.class ~ QICoreCommon."inpatient encounter"
      or Enc.class ~ QICoreCommon."inpatient acute"
      or Enc.class ~ QICoreCommon."inpatient non-acute"
      or Enc.class ~ QICoreCommon."short stay"

define "SDE Specimens":
  [USCoreSpecimenProfile] sp
    where exists ( "Initial Population" IP
        where "Normalize Interval"(sp.collection.collected) overlaps IP.period
    )
    return {
      id: sp.id,
      type: sp.type
    }

// ServiceRequest SDE - regular requests (doNotPerform is false or absent)
define "SDE Service Request Base": [ServiceRequest] ServiceRequests where exists ( "Initial Population" IP where ServiceRequests.authoredOn during IP.period ) return { id: ServiceRequests.id, meta: ServiceRequests.meta, instantiatesCanonical: ServiceRequests.instantiatesCanonical, instantiatesUri: ServiceRequests.instantiatesUri, basedOn: ServiceRequests.basedOn, replaces: ServiceRequests.replaces, requisition: ServiceRequests.requisition, status: ServiceRequests.status, intent: ServiceRequests.intent, category: ServiceRequests.category, priority: ServiceRequests.priority, //doNotPerform: ServiceRequests.doNotPerform,
code: ServiceRequests.code, orderDetail: ServiceRequests.orderDetail, quantity: ServiceRequests.quantity, subject: ServiceRequests.subject, encounter: ServiceRequests.encounter, occurrence: ServiceRequests.occurrence, asNeeded: ServiceRequests.asNeeded, authoredOn: ServiceRequests.authoredOn, requester: ServiceRequests.requester, performerType: ServiceRequests.performerType, performer: ServiceRequests.performer, locationCode: ServiceRequests.locationCode, reasonCode: ServiceRequests.reasonCode, reasonReference: ServiceRequests.reasonReference, insurance: ServiceRequests.insurance, bodySite: ServiceRequests.bodySite, note: ServiceRequests.note, patientInstruction: ServiceRequests.patientInstruction }

// SDE Medication Request - regular requests (doNotPerform is false or absent)
define "SDE Medication Request Base": [MedicationRequest] MedRequests where exists ( "Initial Population" IP where MedRequests.authoredOn during IP.period ) return { id: MedRequests.id, meta: MedRequests.meta, status: MedRequests.status, statusReason: MedRequests.statusReason, intent: MedRequests.intent, category: MedRequests.category, priority: MedRequests.priority, //doNotPerform: MedRequests.doNotPerform,
reported: MedRequests.reported, medication: MedRequests.medication, subject: MedRequests.subject, encounter: MedRequests.encounter, authoredOn: MedRequests.authoredOn, requester: MedRequests.requester, recorder: MedRequests.recorder, reasonCode: MedRequests.reasonCode, reasonReference: MedRequests.reasonReference, instantiatesCanonical: MedRequests.instantiatesCanonical, instantiatesUri: MedRequests.instantiatesUri, courseOfTherapyType: MedRequests.courseOfTherapyType, dosageInstruction: MedRequests.dosageInstruction }

// DiagnosticReport SDEs


define "SDE DiagnosticReport Lab":
  [DiagnosticReportLab] dr
    where exists ( "Initial Population" IP
        where "Normalize Interval"(dr.effective) overlaps IP.period
    )

// ----------------------------------------------------------------------
// Core retrieves
// ----------------------------------------------------------------------

// Locations from IP encounters





define "Get Locations from IP Encounters in Measurement Period":
  flatten ( "Initial Population" IP
      let locationElements: IP.location
      return locationElements LE
        let locationReference: LE.location
        return locationReference.getLocation ( )
  )

/*
@description: Returns true if the given reference is to the given resource
@comment: Returns true if the `id` element of the given resource exactly equals the tail of the given reference.
NOTE: This function assumes resources from the same source server.
*/
define fluent function references(reference Reference, resource Resource):
  resource.id = Last(Split(reference.reference, '/'))

/*
@description: Returns the tail of the given uri (i.e. everything after the last slash in the URI).
@comment: This function can be used to determine the logical id of a given resource. It can be used in
a single-server environment to trace references. However, this function does not attempt to resolve
or distinguish the base of the given url, and so cannot be used safely in multi-server environments.
*/
define fluent function getId(uri String):
  Last(Split(uri, '/'))

